import axios from "axios";
import React, {useEffect, useState} from 'react';
import { useHistory} from "react-router-dom";
import {Card, Button} from 'react-bootstrap'
const MemberOfList = (props) => {
    const [group, setGroup] = useState([])

    useEffect(() => {
        const load_g = async ()=>{
            let res1 = await axios.get('http://127.0.0.1:8000/api/groups/')
            await setGroup(res1.data)

            console.log(group)

        }
        load_g()
  
    
      },[])

    const history = useHistory();
    const RouteChange = (np, group_id, group_name, owner)=> {
        let path = np;
        history.push({
            pathname: path,
            state:{ gid: group_id, gname: group_name, owner: owner}
        })
      }

    return(
        <Card className="group-item" style={{width: "18rem"}}>
            <Card.Body>  
                <Card.Title>{group?.name} </Card.Title>
                <Card.Text>{group?.description}</Card.Text>
                <Button onClick={() =>RouteChange('/tasks', props.gid, group?.name, group?.owner)}variant="secondary"> View</Button>
            </Card.Body>  
        </Card>
    )
};

export default MemberOfList;
