import axios from "axios";
import React, {useEffect, useState} from 'react';
import GroupList from '../components/GroupList'
import useAxios from "../utils/useAxios";

function Home(){
    const [flag, setFlag] = useState(false)
    const [groupList, setGroupList] = useState([])
    const [user_id, setUserid] = useState(0)
    const api = useAxios();

    const changeFlag = () =>{
      setFlag(!flag)
    }
    useEffect(() => {
      const load_g = async ()=>{
        
        const response = await api.get("/userID/");
        setUserid(response.data.response + 1);

        axios.get('http://127.0.0.1:8000/api/groups/')
        .then(result =>{
            setGroupList(result.data)
        })
        
      }
      load_g()

  
    }, [flag])

    return(
    <section className="Home">
      <div className="owned-groups">
      {groupList?.filter(group => group.owner === 3).map((group) =>{
        return <GroupList key = {group.id - 1} name = {group.name} description = {group.description} changeFlag = {changeFlag}/>
      })}
      <p>{user_id}</p>
      </div>
    </section>
      )
  }

  export default Home;
