import React, {useEffect, useState} from 'react';
import GroupList from '../components/GroupList'
import { global_username } from "../context/AuthContext";

const user_name = global_username
let global_user_id = 0

function Home(){
    const [flag, setFlag] = useState(false)
    const [groupList, setGroupList] = useState([])

    const changeFlag = () =>{
      setFlag(!flag)
    }
    useEffect(() => {
      const load_g = async ()=>{
        axios.get('http://127.0.0.1:8000/api/users/')
        .then(result =>{
            let i = 0
            for(i in result.data){
                if (result.data[i]['username'] === user_name){
                    global_user_id = result.data[i]['id']
                }
            }
            console.log(user_name)
            console.log(global_user_id)
        })

        axios.get('http://127.0.0.1:8000/api/groups/')
        .then(result =>{
            setGroupList(result.data)
        })
        
      }
      load_g()

  
    }, [flag])

    return(
    <section className="Home">
      <div className="owned-groups">
      {groupList?.map((group) =>{
        return <GroupList key = {group.id - 1} name = {group.name} description = {group.description} changeFlag = {changeFlag}/>
      })}
      </div>
    </section>
      )
  }

  export default Home;
