import axios from "axios";
import React, {useEffect, useState} from 'react';
import GroupList from '../components/GroupList'
import MemberOfList from '../components/MemberofList'
import useAxios from "../utils/useAxios";
import 'bootstrap/dist/css/bootstrap.min.css'
import { Container, Row, Col, Form, Button } from "react-bootstrap";


function Home(){
    const [flag, setFlag] = useState(false)
    const [groupList, setGroupList] = useState([])
    const [memberOfList, setMemberOfList] = useState([])
    const [username, setUsername] = useState(0)
    const api = useAxios();
    const [newGroupName, setNewGroupName] = useState('')
    const [newGroupDescription, setNewGroupD] = useState('')
    const [memberOfGroups, setMemberOfGroups] = useState([])

    const changeFlag = () =>{
      setFlag(!flag)
    }
    useEffect(() => {
      const load_g = async ()=>{
        

        const un = await api.get("/username/");
        setUsername(un.data.response);

        axios.get('http://127.0.0.1:8000/api/groups/')
        .then(result =>{
            setGroupList(result.data)
        })

        let res = await axios.get('http://127.0.0.1:8000/api/group_member/')
        await setMemberOfList(res.data)

        let i = 0
        let mg = []
        for (i in memberOfList){
            let j = 0
            for( j in groupList){
                if (memberOfList[i]['groups'] === groupList[j]['id']){
                    console.log('hi')
                }        
            }
        }
        
      }
      load_g()

  
    }, [flag])

    const AddGroup = async () =>{
        let formField = new FormData()

        formField.append('name', newGroupName)
        formField.append('description', newGroupDescription)
        formField.append('owner', username)

        await axios({
            method: 'post',
            url: 'api/groups/',
            data: formField
        }).then(res =>{
            console.log(res.data)
        })
        changeFlag()
    }

    return(
    <Container className="Home">
        <Row>
            <Col xs={12} md={8}>
                <h3>Create New Group</h3>
                <Form.Label>Group Name</Form.Label>
                <Form.Control type="text" id="name" onChange={(e) => setNewGroupName(e.target.value)}/>
                <Form.Label>Description</Form.Label>
                <Form.Control type="text" id="Description" onChange={(e) => setNewGroupD(e.target.value)}/>
                <br></br>
                <Button onClick={() => AddGroup()}> Create Group</Button>
            </Col>
        </Row>
        <hr></hr>
        <h3> Welcome {username}, Groups you own</h3>
        <Row xs={1} lg={3}>
        {groupList?.filter(group => group.owner === username).map((group) =>{
            return( 
                <Col key = {group.id}>
                    <GroupList key = {group.id - 1} id = {group.id} name = {group.name} description = {group.description} owner = {group.owner} changeFlag = {changeFlag}/>
                    <br key = {group.id + 1}></br>
                </Col>
            )})}
        </Row>
        <hr></hr>
        <h3> You are member of</h3>
        <Row xs={1} lg={3}>
        {memberOfList?.filter(memof => memof.members === username).map((memof) =>{
            return( 
                <Col key = {memof.id}>
                    <MemberOfList key = {memof.id - 1} gid = {memof.groups}  changeFlag = {changeFlag}/>
                    <br key = {memof.id + 1}></br>
                </Col>
            )})}
        </Row>
        
    </Container>
      )
  }

  export default Home;
