import axios from "axios";
import React, {useEffect, useState} from 'react';
import GroupList from '../components/GroupList'
import useAxios from "../utils/useAxios";
import { Container, Row, Col } from "react-bootstrap";

function Home(){
    const [flag, setFlag] = useState(false)
    const [groupList, setGroupList] = useState([])
    const [user_id, setUserid] = useState(0)
    const api = useAxios();

    const changeFlag = () =>{
      setFlag(!flag)
    }
    useEffect(() => {
      const load_g = async ()=>{
        
        const response = await api.get("/userID/");
        setUserid(response.data.response);

        axios.get('http://127.0.0.1:8000/api/groups/')
        .then(result =>{
            setGroupList(result.data)
        })
        
      }
      load_g()

  
    }, [flag])

    return(
    <Container className="Home">
      <Row className="owned-groups" xs={1} lg={3}>
        {groupList?.filter(group => group.owner === user_id).map((group) =>{
        return( 
            <Col>
                <GroupList key = {group.id - 1} name = {group.name} description = {group.description} changeFlag = {changeFlag}/>
            </Col>
        )})}
      </Row>
    </Container>
      )
  }

  export default Home;
