import axios from "axios";
import React, {useEffect, useState} from 'react';
import GroupList from '../components/GroupList'
import useAxios from "../utils/useAxios";

let global_user_id = 0

function Home(){
    const [flag, setFlag] = useState(false)
    const [groupList, setGroupList] = useState([])
    const [username, setUsername] = useState("")
    const [user_id, setUserid] = useState(0)
    const api = useAxios();

    const changeFlag = () =>{
      setFlag(!flag)
    }
    useEffect(() => {
      const load_g = async ()=>{
        
        const response = await api.get("/username/");
        setUsername(response.data.response);
        console.log(username)
        axios.get('http://127.0.0.1:8000/api/users/')
        .then(result =>{
            let i = 0
            for(i in result.data){
                if(result.data[i]['username'] === username){
                    setUserid(result.data[i]['id'])
                }
            }
        })

        console.log(user_id)

        axios.get('http://127.0.0.1:8000/api/groups/')
        .then(result =>{
            setGroupList(result.data)
        })
        
      }
      load_g()

  
    }, [flag])

    return(
    <section className="Home">
      <div className="owned-groups">
      {groupList?.map((group) =>{
        return <GroupList key = {group.id - 1} name = {group.name} description = {group.description} changeFlag = {changeFlag}/>
      })}
      </div>
    </section>
      )
  }

  export default Home;
